//Takes the text from the user and injects 0-3 "yeezy-based" emojis in randomly selected places
function yeezyfy() {
    var textVal = document.getElementById("text");
    var FinalText = document.getElementById("FinalText");
    var yeezy = ["😎", "💯", "🏄🏾", "🐐", "🌊", "🌊", "🐻", "🎓", "❤", "🔁", "🎤", "🐝"];
    textVal = textVal.value.split(" ");
    for (var i = 0; i < textVal.length; i++) {
        var random = (Math.floor(Math.random() * (12)));
        var random2 = (Math.floor(Math.random() * (2)));
        var random3 = (Math.floor(Math.random() * (3)));

        //A 1 in 2 chance of placing an emoji
        if (random2 == 1) {
            textVal[i] = textVal[i] + yeezy[random];
        }//A 1 in 3 Chance of having more than one emoji
        if (random3 == 2) {
            var random4 = (Math.floor(Math.random() * (4)));

            //The max number of emojis that can be placed is 3
            for (var j = 0; j < random4; j++) {
                var random5 = (Math.floor(Math.random() * (12)));
                textVal[i] = textVal[i] + yeezy[random5];

            }
        }

    }

    //Updating the screen with the emojis
    textVal = textVal.join(" ");
    FinalText.innerHTML = textVal;
}

//Clears both the text field and the "emojified" result
function clear() {
    var textVal = document.getElementById("text");
    var FinalText = document.getElementById("FinalText");

    textVal.value = null;
    FinalText.innerHTML = "";

    $('#copyBtn').hide();
    $('#clearBtn').hide();
}

